            margin="normal"
            required
            fullWidth
            name="roomId"
            label="Room ID"
            type="roomId"
            id="roomId"
            value={roomId}
            onChange={e => setRoomId(e.target.value)}
          />
          <Button
            type="submit"
            fullWidth
            id='play'
            variant="contained"
            sx={{ mt: 3, mb: 2 }}
          >
            Play
          </Button>
        </Box>
      </Box>
    </Container>
  );
}

export default LobbyPage
